async function asciiIntro(e,t,n=22){let r=t.trim().trim();document.getElementById(e).innerText="";let g=r.split("\n"),s=["*","-","/","+","=","#"],f=[];for(let t=0;t<g.length;t++)f.push(Math.floor(6*Math.random()));for(let t=0;t<=g[0].length+1;t++)!function(t){let r="";for(let n=0;n<g.length;n++){var l=Math.floor(g[n].length/2);let e=g[n].substring(l-t,l+t);if(t<g[n].length/2-1)for(let t=0;t<f[n];t++){var o=Math.floor(Math.random()*s.length);e=(i=e,h=t,o=s[o],i.substring(0,h)+o+i.substring(h+o.length))}var a=l-t,l=g[n].length-(l+t);r+=" ".repeat(a<0?0:a)+e+" ".repeat(l<0?0:l)+"\n"}var i,h;document.getElementById(e).innerText=r}(t),await new Promise(t=>setTimeout(t,n))}