async function asciiIntro(t,e,n=22){document.getElementById(t).innerText="";let l=e.split("\n"),o=["*","-","/","+","=","#"],r=[];for(let t=0;t<l.length;t++)r.push(Math.floor(6*Math.random()));function a(e){let n="";for(let t=0;t<l.length;t++){let g=Math.floor(l[t].length/2),s=l[t].substring(g-e,g+e);if(e<l[t].length/2-1)for(let e=0;e<r[t];e++){const t=Math.floor(Math.random()*o.length);h=e,i=o[t],s=(a=s).substring(0,h)+i+a.substring(h+i.length)}let f=g-e,u=l[t].length-(g+e);n+=" ".repeat(f<0?0:f)+s+" ".repeat(u<0?0:u)+"\n"}var a,h,i;document.getElementById(t).innerText=n}for(let t=0;t<=l[0].length+1;t++)a(t),await new Promise((t=>setTimeout(t,n)))}